css:
.certificate-table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 20px;
        font-size: 12px;
    }
    .certificate-table, .certificate-table th, .certificate-table td, .signature-table, .signature-table td {
        border: 1px solid black;
    }
    .certificate-table th, .certificate-table td {
        padding: 6px;
        vertical-align: top;
    }
    .title {
        text-align: center;
        font-weight: bold;
        font-size: 16px;
        text-transform: uppercase;
        padding-top:15px;
    }
    .subtitle {
        text-align: right;
        font-size: 12px;
        font-style: italic;
    }
    .approval-section {
        margin-top: 20px;
        font-size: 12px;
    }
    .signature-table {
        width: 100%;
        margin-top: 30px;
        font-size: 12px;
    }
    .signature-table td {
        padding: 10px;
        vertical-align: top;
    }
    /*.borderless td {*/
    /*    border: none !important;*/
    /*}*/
    .right{
        text-align:right;
    }
    .border-right{
        border-right:none !important;
    }
    .text-center{
        text-align:center;
    }
    .tr-height{
        height:50px;
    }
    .padding-left{
        padding-left: 15px;
    }
    .padding-top{
        padding-top:10px;
    }
