css:
  body {
    font-family: Arial, sans-serif;
    font-size: 14px;
  }

  table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 15px;
  }

  td, th {
    border: 1px solid #000;
    padding: 6px;
    text-align: left;
  }

  .no-border td {
    border: none;
    padding: 5px;
    vertical-align: top;
  }
  .form-header {
    width: 100%;
    table-layout: fixed;
  }
  .col-75 {
    width: 65%;
  }
  .col-25 {
    width: 35%;
  }

  .center {
    text-align: center;
  }


  .toc-wrapper {
    display: flex;
    margin-top: 20px;
  }


.toc-table {
  width: 50%;
}

.toc-table table {
  width: 100%;
  border-collapse: collapse;
}

.toc-table th,
.toc-table td {
  border: 1px solid #000;
  padding: 6px;
  text-align: left;
}

.center {
  text-align: center;
}


.header-section td {
   border: none;
   padding: 6px 3px;
}

  .form-title {
    text-align: center;
    font-weight: bold;
    margin: 10px 0;
  }


.checkbox-section {
  display: flex;
  align-items: center;
  font-size: 14px;
  gap: 10px;
  margin: 10px 0;
}

.checkbox-label {
  display: flex;
  align-items: center;
  gap: 4px;
}

.checkbox {
  width: 15px;
  height: 15px;
  border: 1px solid black;
  display: inline-block;
  text-align: center;
  font-weight: bold;
  font-size: 11px;
  line-height: 12px;
  vertical-align: middle;
}

  .letterhead-table td {
    border: none;
    vertical-align: middle;
    padding-bottom: 10px;
  }

  .logo-img {
    width: 80px;
    height: 80px;
  }
